    else
    {
      $q = LsDoctrineQuery::create()
        ->from('Alias a')
        ->where('a.entity_id = ? AND a.name = ?', array($entity->id, $name));

      if ($matchContext)
      {      
        $q->addWhere('a.context = ?', $context);
      }
        
      if ($q->count())
      {
        return true;
      }
    }
    

    return false;
  }
  
  
