    $emailIds = $stmt->fetchAll(PDO::FETCH_COLUMN);  
    if (count($emailIds))
		{
			$contact_arr['Email'] = '(r.object_model = ? AND r.object_id IN (' . implode(',', $emailIds) . '))';
		}
    
    $sql = 'SELECT DISTINCT p.id FROM phone p WHERE p.entity_id = ?';
